import React, { Component } from 'react';
import './App.css';
import NavBar from './components/NavBar'
import HomeBody from './components/HomeBody'
import SpaceManagerList from './components/spacemanagerlist'
const url = 'https://2cc78895.ngrok.io/currentusers'

class App extends Component {
  constructor() {
    super()
    this.state = {
      customers: [],
      navSearch: false,
      isLoggedIn: false,
      dummyData:[
        {
          licPlate: "xyz123",
          name: "Mike",
          time: 8
        },
        {
          licPlate: "abc987",
          name: "Zeke",
          time: 6
        }
      ]
    }
  }
  async componentDidMount() {
    setInterval(() => this.getData(), 2000)
  }

  getData = async() => {
    const response = await fetch(url)
    const json = await response.json()
    this.setState({ customers: json })
  }

  userLogged = () => this.setState({
    navSearch: !this.state.navSearch,
    isLoggedIn: true
  })

  render() {
    return (
      <>
        <div className="App">
          <NavBar
            navSearch={this.state.navSearch}
            userLogged={this.userLogged}/>
        </div>
        {this.state.isLoggedIn ?
          <div className="row spacemanager">
            <div className="col s2"></div>
            <SpaceManagerList isLoggedIn={this.state.isLoggedIn} dummyData={this.state.dummyData} customers={this.state.customers}/>
            <div className="col s2"></div>
          </div> :
          <div>
            <HomeBody />
          </div>}
      </>
    );
  }
}

export default App;
